package main

import (
	"bytes"
	"fmt"
	"math"
	"strings"
	"time"
)

func commaf(s string) string {
	n := len(s)
	n3 := int(math.Ceil(float64(n) / 3))
	if n3 == 1 {
		return s
	}
	b := []string{}
	for i := 0; i < (n3 - 1); i++ {
		b = append(b, s[i*3:(i+1)*3])
	}
	b = append(b, s[(n3-1)*3:])
	return strings.Join(b, ",")
}

func comma(s string) string {
	n := len(s)
	if n <= 3 {
		return s
	}
	return comma(s[:n-3]) + "," + s[n-3:]
}

func comma2(s string) string {
	n := len(s)
	if n <= 3 {
		return s
	}
	na := n / 3
	nb := n % 3
	var buf bytes.Buffer
	if nb > 0 {
		buf.WriteString(s[:nb])
		buf.WriteString(",")
		s = s[nb:]
	}
	for i := 0; i < (na - 1); i++ {
		buf.WriteString(s[i*3 : (i+1)*3])
		buf.WriteString(",")
	}
	buf.WriteString(s[(na-1)*3 : na*3])
	return buf.String()
}
func main() {
	start1 := time.Now()
	fmt.Println(comma(""))
	fmt.Println(time.Since(start1).Seconds())
	start2 := time.Now()
	fmt.Println(comma2(""))
	fmt.Println(time.Since(start2).Seconds())
}
